const initState = {
    filter: {
        search: '',
        status: '',
        properties: []
    },

    todoList: [{
        id: 1,
        content: "Learn React",
        complete: true,
        properties: "HIGH"
    }]
}
export const rootReducer = (state = initState, action) => {
    switch (action.type) {
        case "todoList/addTodo":
            return {
                ...state,
                todoList: [
                    ...state.todoList,
                    action.payload
                ]
            }
        case "EDIT_STATUS":
            return state.map(item => {
                if (item.id === action.payload) {
                    return {
                        ...item,
                        complete: !item.complete
                    };
                }
                return item;
            })
        default:
            return state;
    }
}
