import filter from "../List4/Filter";

const initState =
    [{
        id: 1,
        name: "Learn React",
        complete: true,
        properties: "HIGH"
    }]

export const todoListReducer = (state = initState, action) => {
    switch (action.type) {
        case "todoList/addTodo":
            return {
                ...state,
                todoList: [
                    ...state.todoList,
                    action.payload
                ]
            }
        case "EDIT_STATUS":
            return state.map(item => {
                if (item.id === action.payload) {
                    return {
                        ...item,
                        complete: !item.complete
                    };
                }
                return item;
            })
        default:
            return state;
    }
}
