import {createSelector} from "reselect";

export const todoListSelector = (state) => {
    return state.todoList.filter(item => {
        return item.name.includes(searchTextSelector(state))
    })
}

export const searchTextSelector = (state) => state.filter.search;
export const filterStatusSelector = (state) => state.filter.status;
export const filterPropertiesSelector = (state) => state.filter.properties;

export const todosRemainingSelector = createSelector(
    todoListSelector,
    filterStatusSelector,
    searchTextSelector,
    filterPropertiesSelector,
    (todoList, status, searchText,properties) => {
        return todoList.filter(item => {
            if(status==="ALL") {
                console.log(item.name.toUpperCase());
                console.log(searchText.toUpperCase())
                return item.name.toUpperCase().includes(searchText.toUpperCase())
            }
            return (
                item.name.includes(searchText) &&
                status === "COMPLETE" ? item.complete : !item.complete
            )
        })
    })
