const initState=[{
    id:1,
    content:"Learn React",
    complete:true
}]
export const todoListReducer=(state=initState,action)=>{
    switch (action.type){
        case "ADD":
            return [...state,action.payload]
        case "EDIT_STATUS":
            return state.map(item=>{
                if(item.id===action.payload){
                    item.complete=!item.complete
                }
            })
        default:
            return state;
    }
}
